defined('_JEXEC') or die();

class CVideoAdapterBing extends CVideoAdapterAbstarct
{
	static private $tamplate = '<iframe width="%s" height="%s" frameborder="0" marginwidth="0" marginheight="0" scrolling="no" src="http://hub.video.msn.com/embed/%s/?"></iframe>';

	public function __construct($params)
	{
		$this->params = $params;
	}
	
	public function check($url)
	{
		$i = preg_match('/.*bing.com\/videos\/browse.*&vid=(.*)&.*/iU', $url, $matches);
		if($i)
		{
			$this->key = $matches[1];
			return true;
		}
			
		return false;
	}
	
	public function getHtml()
	{
		return sprintf(self::$tamplate,
				$this->params->get('width', 350),
				$this->params->get('height', 200),
				$this->key
			);
	}
}