                  <tr>\n                                            <td><code>offset</code></td>\n                                            <td>integer</td>\n                                            <td>5</td>\n                                            <td>Offset to the source element</td>\n                                        </tr>\n                                        <tr>\n                                            <td><code>pos</code></td>\n                                            <td>string</td>\n                                            <td>\'top\'</td>\n                                            <td>Tooltip position</td>\n                                        </tr>\n                                        <tr>\n                                            <td><code>animation</code></td>\n                                            <td>boolean</td>\n                                            <td>false</td>\n                                            <td>Fade in tooltip</td>\n                                        </tr>\n                                        <tr>\n                                            <td><code>delay</code></td>\n                                            <td>integer</td>\n                                            <td>0</td>\n                                            <td>Delay tooltip show in ms</td>\n                                        </tr>\n                                        <tr>\n                                            <td><code>cls</code></td>\n                                            <td>string</td>\n                                            <td>\'\'</td>\n                                            <td>Custom class to add on show</td>\n                                        </tr>\n                                        <tr>\n                                            <td><code>activeClass</code></td>\n                                            <td>string</td>\n                   